document.querySelector("form").addEventListener("submit",(t=>{t.preventDefault(),async function(t){const e=await async function(t){console.log("Getting weather data for "+t);const e=await fetch(`http://api.weatherapi.com/v1/current.json?key=776d934fc4dd4c6e9fd13929240705&q=${t}`),n=function(t){const e={};return e.country=t.location.country,e.city=t.location.name,e.temp_c=t.current.temp_c,e.feelsLike=t.current.feelslike_c,e.humidity=t.current.humidity,e.wind=t.current.wind_kph,e}(await e.json());return console.log(n),n}(t);console.log(e);const n=document.getElementById("cityName"),c=document.getElementById("temperature"),o=document.getElementById("feelsLike"),i=document.getElementById("humidity"),r=document.getElementById("wind");c.textContent=String(e.temp_c)+" °C",n.textContent=e.city+", "+e.country,o.textContent=String(e.feelsLike)+" °C",i.textContent=String(e.humidity)+" %",r.textContent=String(e.wind)+" km/h"}(document.getElementById("city").value)}));